#01 Combien y a-t-il de handle dans cette array ?
puts "Cet array contient  #{twit_handles.length} handles"

#02 Quel est le handle le plus court de cette liste ?
shortest_handle = twit_handles[0]
twit_handles.each do |handle|
  if handle.length < shortest_handle.length
    shortest_handle = handle
  end
end
puts "\nL'handle le plus petit  est #{shortest_handle}."


#03 Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère)

puts "\nLe nombre d'handle à cinq caractères (hors @) est de #{twit_handles.select {|handle| handle.length==5+1}.length}"

#04 Combien commencent par une majuscule (première lettre juste après le @) ?
#verification de l'état de majuscule du 2e caractere ( index[1]) de l'élément
startWithUpperCaseHandle = twit_handles.select{|handle| handle[1].match(/\p{Upper}/)}

puts "\nLe nombre d'handle commançant par une majuscule (hors @) est de #{startWithUpperCaseHandle.length}"

#05 Trie la liste de handle par ordre alphabétique.
puts "\nVoici la liste des handles par ordre alphabétique: \n\n"
#penser à utiliser downcase pour l'ordre ignore la casse du caractere
sorted_handles = twit_handles.sort_by {|handle| handle.downcase}
puts sorted_handles


#06 Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après)
puts "\nVoici la liste des handles par ordre de taille: \n\n"
sorted_handles_by_length = twit_handles.sort_by {|handle| handle.length}
puts sorted_handles_by_length

#07 Quelle est la position dans l'array de la personne @epenser ?
puts  "\nLa position de @epenser dans la liste est au rang #{twit_handles.find_index("@epenser")}."

#08 Répartition des handle par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)
#Déterminons le plus grand nombre de caractère existant
longest_handle = twit_handles[0]
twit_handles.each do |handle|
  if handle.length > longest_handle.length
    longest_handle = handle
  end
end

#09 Selection des elements en fonction du nombre de caractère
# nombre de caractères plus petit au plus grand
#suppression du caractere @
puts shortest = (shortest_handle.length) -1
  puts longest = (longest_handle.length) -1
puts "\nVoici la répartition des handles par nombre de caractère (hors @) :"
for n in shortest..longest
  selected_handles = twit_handles.select{|handle| handle.length ==n+1}
  if selected_handles.length !=0 # pour eviter de mentionner les cas sans handle
    puts "Handle(s) dont le nombre de caractere est de #{n} : #{selected_handles.length}."
  end
end

